mapper.insert:
		INSERT INTO CART(
				MEMBER_ID,
				BOOK_CD,
				QUANTITY
				
		)
		VALUES (
				#{memberId},
				#{bookCd},
				#{quantity}
							
		 )
		ON DUPLICATE KEY UPDATE
        QUANTITY = QUANTITY + VALUES(QUANTITY);

mapper.update:
UPDATE
		CART
SET
		QUANTITY = #{quantity}
WHERE
		CART_CD = #{cartCd}
